#!/bin/bash
# Polynomial regression with Ridge regularization (degree=4)
if [ "$#" -ne 3 ]; then
  echo "0"
  exit 0
fi
D=$1
M=$2
R=$3
result=$(echo "scale=10; -29.054255718943068 + (137.950693673443340)*$D + (0.854287916211915)*$M + (-0.929313759607837)*$R + (-4.120323917005242)*$D^2 + (-0.082709238725247)*$D*$M + (-0.012060641117955)*$D*$R + (-0.000129699864406)*$M^2 + (-0.000444135769102)*$M*$R + (0.003196870211494)*$R^2 + (-0.565431382927947)*$D^3 + (0.011669144103573)*$D^2*$M + (-0.002851815403129)*$D^2*$R + (0.000010862093590)*$D*$M^2 + (0.000013485080900)*$D*$M*$R + (0.000012496357542)*$D*$R^2 + (0.000000028706747)*$M^3 + (0.000000748330328)*$M^2*$R + (-0.000000218494535)*$M*$R^2 + (-0.000001939027112)*$R^3 + (0.037236905177264)*$D^4 + (-0.000384240742730)*$D^3*$M + (0.000153002574393)*$D^3*$R + (-0.000005974534174)*$D^2*$M^2 + (-0.000000115244795)*$D^2*$M*$R + (0.000000301152591)*$D^2*$R^2 + (0.000000066916996)*$D*$M^3 + (0.000000004692518)*$D*$M^2*$R + (-0.000000011224515)*$D*$M*$R^2 + (-0.000000003661429)*$D*$R^3 + (-0.000000000377674)*$M^4 + (0.000000000150268)*$M^3*$R + (-0.000000000413184)*$M^2*$R^2 + (0.000000000189595)*$M*$R^3 + (0.000000000348057)*$R^4" | bc -l)
printf "%.2f\n" "$result"
